    let speak = (t) => {
        let content = ""
        if (t.attachments != null && t.attachments.default_card) {
            // CARD
            content = t.attachments.default_card.description;
            if (t.attachments.default_card.links && t.attachments.default_card.links.length > 0) {
                for (var i = 0; i < t.attachments.default_card.links.length; i++) {
                    content += t.attachments.default_card.links[i].title + " . ";
                }
            }
        } else if (t.attachments != null && t.attachments.quick_reply) {
            // QUICK REPLY
            content = t.content;
            for (var i = 0; i < t.attachments.quick_reply.quick_links.length; i++) {
                content += t.attachments.quick_reply.quick_links[i].title + " . ";
            }
        } else {
            // TEXT
            console.log("text");
            content = t.content;
        }
        content = content.replace(/
/g, '')
            .replace(//g, '')
            .replace(/<\/b>/g, '')
            .replace(/
/g, '')
            .replace(//g, '').replace(/<\/table>/g, '').replace(/<\/tr>/g, '').replace(/
/g, '')
            .replace(/<\/td>/g, '').replace(//g, '').replace(/<\/th>/g, '').replace(/
/g, '')
            .replace(/(<((?!(\/?)say-as)[^>]+)>)/ig, '')
            .replace(/(?:\s?)(LIC)(?:\s)/ig, 'LIC')
            .replace(/(?:\s?)(एलआईसी)(?:\s)/ig, 'LIC')
            .replace(/(?:\s?)(यह)(?:\s)/ig, 'ये ')
            .replace(/ |/, '')
            .replace(/\.(?!\s)/g, '. ');
        if (hasHindiCharacters(content)) {
            let includesNum = content.match(/[0-9\-]+/g);
            if (includesNum) {
                includesNum.forEach(num => {
                    let num1;
                    if (num.length > 6) {
                        num1 = num.replace(/1/ig, '१ ')
                            .replace(/2/ig, '२ ')
                            .replace(/3/ig, '३ ')
                            .replace(/4/ig, '४ ')
                            .replace(/5/ig, '५ ')
                            .replace(/6/ig, '६ ')
                            .replace(/7/ig, '७ ')
                            .replace(/8/ig, '८ ')
                            .replace(/9/ig, '९ ')
                            .replace(/0/ig, '० ')
                            .replace(/-/ig, '- ')
                            .replace(/-/ig, "- ")
                    } else {
                        num1 = num.replace(/1/ig, '१')
                            .replace(/2/ig, '२')
                            .replace(/3/ig, '३')
                            .replace(/4/ig, '४')
                            .replace(/5/ig, '५')
                            .replace(/6/ig, '६')
                            .replace(/7/ig, '७')
                            .replace(/8/ig, '८')
                            .replace(/9/ig, '९')
                            .replace(/0/ig, '०')
                    }
                    console.log(num, num1)
                    content = content.replace(num, num1)

                })

            }
        }
        console.log("content", content);
