            e.appendChild(snackbar);
            var interval = setInterval(function () {
                let mic_elem = document.querySelector('.custom-mic.mic.ptr.enabled');
                var css_class = false;
                console.log("interval being hit");
                if (mic_elem) {
                    mic_elem.addEventListener('click', function (e) {
                        console.log("cleared interval");
                        clearInterval(interval);
                    });
                }
                if (navigator.connection && navigator.connection.effectiveType != '4g' && !pop_up &&
                    mic_elem) {
                    console.log("display snack bar");
                    snackbar.className = 'show';
                    pop_up = true;
                    css_class = true
                } else if (navigator.connection && navigator.connection.effectiveType != '4g' && pop_up &&
                    mic_elem) {
                    console.log("delete snack bar");
                    snackbar.className = snackbar.className.replace("show", "");
                    css_class = false
                    pop_up = false;
                }
            }, 3000);
        }
        console.log(">>>>Before IF FOR TOGGLE")
        var e = document.getElementById("avaamo__popup");
        var mim_button = document.createElement("div");
        mim_button.id = "id_minimize_btn";
        mim_button.classList.add("minimize__btn");
        e.appendChild(mim_button);
        document.getElementById("id_minimize_btn").addEventListener("click", function () {
            let audio = document.querySelector('#avaamoaudio');
            audio.pause();
            audio.currentTime = 0;
            Avaamo.closeChatBox();
            document.getElementsByClassName('avaamo__icon')[0].classList.add("minimizeState");
        });
        document.getElementsByClassName("avaamo_popup__close")[0].addEventListener("click", function () {
            console.log("In the event list");
            document.getElementsByClassName('avaamo__icon')[0].classList.remove("minimizeState");
        });
    });

